import { Button } from "@mui/material";
import React, { useState } from 'react';
import "../../Css/style.css";
import logo from "../../assets/Saarthi-blue.png";
import TestThree from "./TestThree";
import TestOne from "./TestOne";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';


const TestTwo = ({ allAnswer, view })=> {
  const [question, setQuestion] = useState(allAnswer);
   const [testtwoView, setTestTwoView] = useState(view);

  const containerStyle = {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: '0.1rem',
  };
  const handleAnswerClick = (e, a1,option) => {
    let newQuestion = question;

    newQuestion.map((obj, index) => {
      if (obj.QuestionNumber === option.QuestionNumber) {
        if (a1 === obj.correctAnswer) {
          obj.status = 1;
        }
        else {
          obj.status = 0;
        }

      }
    });
    setQuestion(newQuestion);
    const buttons = document.querySelectorAll('.buttonStyle');
  buttons.forEach((button) => {
    button.classList.remove('selected');
     button.style.outline = 'none'; 
  });
  e.currentTarget.classList.add('selected');
  e.currentTarget.style.outline = '2px solid green';
  };
  const handleSubmit = () => {
    let isAllSelected = true;
    question.forEach((option) => {
      if (option.LevelNumber === 178 && option.status === '') {
        isAllSelected = false;
      }
    });
    if (isAllSelected) {
      let newQuestion = question;
      const count = newQuestion.reduce((count, question) => {
        if (question.LevelNumber === 178 && question.status === 1) {
          return count + 1;
        }
        return count;
      }, 0);

      if (count > 3) {
        setTestTwoView(3)
      } 
      else {
        setTestTwoView(1)
      }

      setQuestion(newQuestion);
    } else {
      alert("Please select an option for all questions.");
    }
  };
  if (testtwoView === 2) {
    return (
      <>
        <div class="form-group1">
          <img class="img" src={logo} alt="Company Logo" />
        </div>

        <div class="container ">
          <div>
            <span class="heading">Answer The Following Questions</span>
          </div>
          <div class="main pb-0 ">
            <div class="span">
              <span class="s1">Topic : What comes after</span>
            </div>
            {question.map((option, index) =>
              option.LevelNumber === 178 ? (
                <div className="qus">
                  <span className="Qus-num">Q{option.QuestionNumber} . {option.question}</span>
                  <div style={containerStyle}>
                    {option.options.map((a1, optionIndex) => (
                      <Button
                        class="buttonStyle"
                        onClick={(e) => handleAnswerClick(e, a1, option)}
                      >
                        {a1}
                      </Button>
                    ))}
                  </div>
                </div>
              ) : null
            )}
            <div class="sub-btn">
              <Button class="Assessment-btn mb-5" onClick={handleSubmit}>
                Submit
              </Button>
            </div>
          </div>
        </div>
      </>
    );
  } else if (testtwoView === 3) {
    return <TestThree allAnswer={question} view={3} />;
  } else if (testtwoView === 1) {
    return <TestOne allAnswer={question} view={1} />;
  } else {
    return null;
  }
};

export default TestTwo;
