import { Button,Input } from "@mui/material";
import React, { useState } from 'react';
import "../../Css/style.css";
import logo from "../../assets/Saarthi-blue.png";
import Scoreboard from "../Scoreboard/index";
import TestThree from "./TestThree";
const containerStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr',
  gap: '0.1rem',
};
const TestFour = ({allAnswer,view}) => {
  const [question, setQuestion] = useState(allAnswer);
  const [testFourView, setTestFourViewView] = useState(view);

  const handleAnswerChange = (e, option) => {
    let newQuestion = [...question];
    newQuestion.forEach((obj) => {
      if (obj.QuestionNumber === option.QuestionNumber) {
        if (e.target.value === obj.correctAnswer) {
          obj.status = 1;
        }
        else {
          obj.status = 0;
        }
      }
    });
    setQuestion(newQuestion);
    const buttons = document.querySelectorAll('.buttonStyle');
    buttons.forEach((button) => {
      button.classList.remove('selected');
       button.style.outline = 'none'; 
    });
    e.currentTarget.classList.add('selected');
    e.currentTarget.style.outline = '2px solid green';
  };

const handleSubmit = () => {
  let isAllSelected = true;
  question.forEach((option) => {
    if (option.LevelNumber === 235 && option.status === '') {
      isAllSelected = false;
    }
  });
  if (isAllSelected) {
    let newQuestion = question;
    const count = newQuestion.reduce((count, question) => {
      if (question.LevelNumber === 235 && question.status === 1) {
        return count + 1;
      }
      return count;
    }, 0);

    if (count > 3) {
      setTestFourViewView(5)
    }    
    else {
      setTestFourViewView(3)
    }
    setQuestion(newQuestion);
  } else {
    alert("Please select an option for all questions.");
  }
};

if (testFourView === 4) {
  return (
    <>
      <div class="form-group1">
        <img class="img" src={logo} alt="Company Logo" />
      </div>

      <div class="container ">
        <div>
          <span class="heading">Answer The Following Questions</span>
        </div>
        <div class="main pb-0 ">
          <div class="span">
            <span class="s1">Topic : Identify smaller number</span>
          </div>
          {question.map((option, index) =>
            option.LevelNumber === 235 ? (
              <div className="qus">
                <span className="Qus-num">Q{option.QuestionNumber} . {option.question}</span>
                <div style={containerStyle}>
                  <Input
                    variant="outlined"
                    onChange={(e) => handleAnswerChange(e, option)}
                    style={{ margin: "0.5rem" }}
                  />
                </div>
              </div>
            ) : null
          )}
          <div class="sub-btn">
            <Button class="Assessment-btn mb-5" onClick={handleSubmit}>
              Submit
            </Button>
          </div>
        </div>
      </div>
    </>
  );
} else if (testFourView === 5) {
  return <Scoreboard allAnswer={question} />;
} else if (testFourView === 3) {
  return <TestThree allAnswer={question} testtwoView={3} />;
} else {
  return null;
}
};

export default TestFour;
