import { Button } from "@mui/material";
import React, { useState } from 'react';
import "../../Css/style.css";
import logo from "../../assets/Saarthi-blue.png";
import Questions from '../Questions.json';
import TestFour from "./TestFour";
import TestTwo from "./TestTwo";
const containerStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr',
  gap: '0.1rem',
};
const TestThree = ()=> {
  const [question, setQuestion] = useState(Questions);
  const [view, setView] = useState(3);

 const handleAnswerClick = (e, a1,option) => {
   let newQuestion = question;

   newQuestion.map((obj, index) => {
     if (obj.QuestionNumber === option.QuestionNumber) {
       if (a1 === obj.correctAnswer) {
         obj.status = 1;
       }
       else {
         obj.status = 0;
       }

     }
   });
   setQuestion(newQuestion);
   const buttons = document.querySelectorAll('.buttonStyle');
  buttons.forEach((button) => {
    button.classList.remove('selected');
     button.style.outline = 'none'; 
  });
  e.currentTarget.classList.add('selected');
  e.currentTarget.style.outline = '2px solid green';
 };

const handleSubmit = () => {
  let isAllSelected = true;
  question.forEach((option) => {
    if (option.LevelNumber === 220 && option.status === '') {
      isAllSelected = false;
    }
  });
  if (isAllSelected) {
    let newQuestion = question;
    const count = newQuestion.reduce((count, question) => {
      if (question.LevelNumber === 220 && question.status === 1) {
        return count + 1;
      }
      return count;
    }, 0);

    if (count > 3) {
      setView(4)
    } 
    else {
      setView(2); 

    }

    setQuestion(newQuestion);
  } else {
    alert("Please select an option for all questions.");
  }
};

if (view === 3) {
  return (
    <>
      <div class="form-group1">
        <img class="img" src={logo} alt="Company Logo" />
      </div>

      <div class="container ">
        <div>
          <span class="heading">Answer The Following Questions</span>
        </div>
        <div class="main pb-0 ">
          <div class="span">
            <span class="s1">Topic : What come before</span>
          </div>
          {question.map((option, index) =>
            option.LevelNumber === 220 ? (
              <div className="qus">
                <span className="Qus-num">Q{option.QuestionNumber} . {option.question}</span>
                <div style={containerStyle}>
                  {option.options.map((a1, optionIndex) => (
                    <Button
                      class="buttonStyle"
                      onClick={(e) => handleAnswerClick(e, a1, option)}
                    >
                      {a1}
                    </Button>
                  ))}
                </div>
              </div>
            ) : null
          )}
          <div class="sub-btn">
            <Button class="Assessment-btn mb-5" onClick={handleSubmit}>
              Submit
            </Button>
          </div>
        </div>
      </div>
    </>
  );
} else if (view === 4) {
  return <TestFour allAnswer={question} view={4} />;
} else if (view === 2) {
  return <TestTwo allAnswer={question} view={2} />;
} else {
  return null;
}
};

export default TestThree;
